export default function NichesAnimation() {
  const ref = useRef(null);
  const [media] = useAtom(mediaAtom);

  useEffect(() => {
    if (media === 'mobile') {
      return;
    }

    const ctx = gsap.context(() => {
      gsap.to('.__shape', {
        scrollTrigger: {
          trigger: ref.current,
          start: 'top 70%',
          end: 'bottom 50%',
          scrub: true,
        },
        ease: 'power2.inOut',
        rotate: '-20deg',
      });

      gsap.to('.__noise', {
        scrollTrigger: {
          trigger: ref.current,
          start: 'top 70%',
          end: 'bottom 50%',
          scrub: true,
        },
        ease: 'power2.inOut',
        opacity: 0,

        rotate: '22deg',
      });

      gsap.to('.__noise2', {
        scrollTrigger: {
          trigger: ref.current,
          start: 'top 70%',
          end: 'bottom 50%',
          scrub: true,
        },
        ease: 'power2.inOut',
        opacity: 1,
        rotate: `${45 + 22}deg`,
      });

      const textTrigger = {
        trigger: ref.current,
        start: 'top 35%',
        end: 'bottom 80%',
        scrub: true,
      };
      gsap.to('.__left-text', {
        scrollTrigger: textTrigger,
        opacity: 1,
        translateY: 5,
        ease: 'power2.in',

        // top: '55%',
      });

      gsap.to('.__right-text', {
        scrollTrigger: textTrigger,
        ease: 'power2.in',

        opacity: 1,
        translateY: 5,
      });
    }, ref);

    return () => {
      ctx.revert();
    };
  }, [media]);

  return (
    <div
      ref={ref}
      className="relative hidden h-[580px] justify-between overflow-hidden md:flex"
    >
      <div></div>
      <div className="__shape absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2">
        <div className="relative">
          <Shape />
          <Image
            src={Noise}
            alt=""
            className="__noise absolute top-[6.9%] left-[24.1%] h-[440px] w-[440px]"
          />
          <Image
            src={Noise}
            alt=""
            className="__noise2 absolute top-[6.9%] left-[24.1%] h-[440px] w-[440px] rotate-45 opacity-0"
          />
          {/* <div className="has-noise absolute top-[6.9%] left-[24.1%] h-[440px] w-[440px] overflow-hidden rounded-full"></div> */}
        </div>
      </div>
      <div></div>
      <div></div>
      <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2">
        <Logo />
      </div>
      <div className="__left-text opacity-1 flex w-1/2 pt-[68px] pl-16 text-center text-body-s opacity-0 xl:items-end xl:pb-[200px]">
        <div className="max-w-[176px] xl:ml-auto xl:mr-[470px]">
          We take into account the needs of business
        </div>
      </div>
      <div className="__right-text opacity-1 flex w-1/2 items-end justify-end pb-[68px] pr-16 text-center text-body-s opacity-0 xl:items-center">
        <div className="max-w-[176px] xl:mr-auto xl:ml-[470px]">
          We create user-friendly interfaces
        </div>
      </div>
    </div>
  );

  // return <Shape />;
  // return <Logo />;
  // });
}
